<script lang="ts">
  import IntroHeaderPaging from './IntroHeaderPaging.svelte';

  export { className as class };
  let className = '';

  const descriptions = [
    'computer science student',
    'vim enthusiast',
    'web developer',
  ];

  let descIndex = 0;
  setInterval(() => {
    descIndex = (descIndex + 1) % descriptions.length;
  }, 3000);
  $: description = descriptions[descIndex];
</script>

<div class={`${className} flex`}>
  <div>I am a</div>
  <IntroHeaderPaging {description}/>
</div>

