<script lang="ts">
  import IntroHeaderPager from './IntroHeaderPager.svelte';

  export { className as class };
  let className = '';
  export let descriptions: string[] = [];
  $: prefixedDescs = descriptions
      .map(d => ` ${aOrAnPrefix(d)} ${d}`);

  function aOrAnPrefix(word: string): string {
    return isVowelWord(word) ? 'an' : 'a';
  }

  function isVowelWord(word: string): boolean {
    if (word.length === 0) {
      return false;
    }
    return isVowel(word.charAt(0));
  }

  function isVowel(char: string): boolean {
    return 'aeiou'.includes(char);
  }
</script>

<div class={`${className} flex`}>
  <div>I am&nbsp;</div>
  <IntroHeaderPager
    values={prefixedDescs}
    interval={3000}
    duration={400}
    dy={-20}
  />
</div>

