import type { TransitionConfig } from 'svelte/transition';

declare type EasingFunction = (t: number) => number;

interface WipeParams {
    delay?: number;
    duration?: number;
    easing?: EasingFunction;
}

export default (node: Element, {
  delay = 0,
  duration = 400,
  easing
}: WipeParams): TransitionConfig => ({
  delay,
  duration,
  easing,
  css: (t: number) => `
    width: ${t * 100}%;
  `,
});

