export const aOrAnPrefix = (word: string): string => {
  return isVowelWord(word) ? 'an' : 'a';
}

export const isVowelWord = (word: string): boolean => {
  if (word.length === 0) {
    return false;
  }
  return isVowel(word.charAt(0));
}

export const isVowel = (char: string): boolean => {
  return 'aeiou'.includes(char);
}

