export function aOrAnPrefix(word: string): string {
  return isVowelWord(word) ? 'an' : 'a';
}

export function isVowelWord(word: string): boolean {
  if (word.length === 0) {
    return false;
  }
  return isVowel(word.charAt(0));
}

export function isVowel(char: string): boolean {
  return 'aeiou'.includes(char);
}

